        public static int BalancedStringSplit(string s)
        {
            var answer = 0;
            var newSegment = true;

            var charCount = 0;
            var lastChar = new char();


            foreach (var c in s)
            {
                if (newSegment)
                {
                    charCount += 1;
                    lastChar = c;
                    newSegment = false;
                }
                else
                {
                    if (c == lastChar)
                    {
                        charCount += 1;
                    }
                    else
                    {
                        charCount -= 1;
                    }

                    if (charCount == 0)
                    {
                        answer += 1;
                        newSegment = true;
                    }
                }
            }

            return answer;
        }
